<html>
<head>
    <meta name="viewport" content="initial-scale=1.0, user-scalable=no">
    <meta charset="utf-8">
    <title>Simple markers</title>
    <style>
    #map_canvas {
      width: 100%;
      min-height: 100%;
    }
    #loading_container{position: absolute;width: 100%;height: 100%;background: #fff;text-align: center;top: 0;left: 0;}
    #loading_container div{position: absolute;width: 200px;height: 40px;top: 0;bottom: 0;left: 0;right: 0;margin: auto;}
    </style>
</head>
<body id="body" style="overflow: hidden; margin: 0px auto; padding: 0px;">
  
  <div id="map_canvas" class="mapping"></div>
  <script>
  
  document.getElementById("map_canvas").style.height = <?php echo isset($_GET['height']) ? $_GET['height'] : 'screen.height'; ?> +'px';
  var geocoder;
  var map;
  var markersArray = [];
  var locationsArray = <?php echo json_encode($_GET['q']); ?>;
  var infoWindow ;
   
  function is_address_or_lat_lng(val)
  {
    val1 = val.split(",");
    if(val1.length == 2 && isFinite(val1[0]) && isFinite(val1[1]))
      return {type: 'latlng', data: {lat: parseFloat(val1[0]), lng: parseFloat(val1[1])}};
    else
    {
      return {type: 'address', data: geocoder.geocode( { 
         'address': val },                                             
        function(results, status) 
        {  
          if (status == google.maps.GeocoderStatus.OK) 
          {
            map.setCenter(results[0].geometry.location);
          }
        })};
    }
  }

    function initialize() 
    {
      geocoder = new google.maps.Geocoder();
      var infoWindow = new google.maps.InfoWindow(), marker, i;
      var bounds = new google.maps.LatLngBounds();
      


      var myOptions = 
        {
          zoom: 5, 
          mapTypeId: google.maps.MapTypeId.ROADMAP,
          disableDefaultUI: true,
          navigationControlOptions: 
            {
              style: google.maps.NavigationControlStyle.SMALL
            }
        };
      map = new google.maps.Map(document.getElementById("map_canvas"), myOptions);


      if(locationsArray.length == 1)
      {
        google.maps.event.addListenerOnce(map, 'bounds_changed', function(event) {
          if (this.getZoom()){
              this.setZoom(16);
              console.log(this.getZoom());
          }
        });
      }

      plotMarkers();

      function plotMarkers(){
        for(var i = 0; i < locationsArray.length; i++){
          codeAddresses(locationsArray[i], i);
        }
      }
      
      function codeAddresses(address, i){
         
         is_address_or_lat_lng1 = is_address_or_lat_lng(address);

         if(is_address_or_lat_lng1.type == 'latlng')
         {
            myLatlng = new google.maps.LatLng(is_address_or_lat_lng1.data.lat,is_address_or_lat_lng1.data.lng);
            console.log(myLatlng);

            map.setCenter(myLatlng);
            bounds.extend(myLatlng);
            marker = new google.maps.Marker({
              map: map,
              position: myLatlng
            });
        
            google.maps.event.addListener(marker, 'click', (function(marker, i) {
              return function() {
                infoWindow.setContent(address);
                infoWindow.open(map, marker);
              }
            })(marker, i));
          
            map.fitBounds(bounds);

         }
         else
         {
            geocoder.geocode( { 'address': address}, function(results, status) { 
               
               if (status == google.maps.GeocoderStatus.OK) {
                map.setCenter(results[0].geometry.location);
                bounds.extend(results[0].geometry.location);
                marker = new google.maps.Marker({
                  map: map,
                  position: results[0].geometry.location
                });
            
                google.maps.event.addListener(marker, 'click', (function(marker, i) {
                  return function() {
                    infoWindow.setContent(address);
                    infoWindow.open(map, marker);
                  }
                })(marker, i));
              
                map.fitBounds(bounds);
                //markersArray.push(marker); 
              }
              else if (status === google.maps.GeocoderStatus.OVER_QUERY_LIMIT) {
                setTimeout(function() {
                codeAddresses(address, i);
                }, 250 );
              } 
              else{
                //alert("Geocode was not successful for the following reason: " + status);
              }
            });
         }
         
      }

    }

  </script>
  <script src="//maps.googleapis.com/maps/api/js?key=AIzaSyBNzmlJsqs8lqu7R5bcfw1RoWPg-7Pypt8&callback=initialize" ></script>
</body>
</html>